#include<iostream>
#include<vector>
#include<algorithm>
#include<string>
using namespace std;
bool checksubs(string subs, vector<string>& words, int span, int len1, int len2)
{
	vector<string> usewords(words);
	int i = 0;
	for (; i<len1; i += span)
	{
		int j = 0;
		string spanstring = subs.substr(i, span);
		while (j<len2&&spanstring != usewords[j])
			j++;
		if (j == len2)
			return false;
		else
		{
			usewords.erase(usewords.begin() + j, usewords.begin() + j + 1);
			len2--;
		}
	}
	if (usewords.empty() && i == len1)
		return true;
	else
		return false;
}
vector<int> findSubstring(string s, vector<string>& words) {
	int len1 = s.size(), len2 = words.size();
	vector<int> result;
	if (len2 == 0)
		return result;
	int span = words[0].size();
	if (span == 0)
	{
		for (int i = 0; i <= len1; i++)
			result.push_back(i);
		return result;
	}
	int finlength = span*len2;
	vector<string> cheating;     //cheating
	int cheatlen = 0;
	for (int i = 0; i + finlength <= len1; i++)
	{
		string subs = s.substr(i, finlength);
		if (cheatlen>0)
		{
			vector<string>::iterator findingcheat = find(cheating.begin(), cheating.end(), subs);
			if (findingcheat != cheating.end())
				continue;
		}
		if (checksubs(subs, words, span, finlength, len2))
			result.push_back(i);
		else
		{
			cheating.push_back(subs);
			cheatlen++;
		}
	}
	return result;
}
